sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var glideSysAttachment = new GlideSysAttachment();
	var sysAttachGR = new GlideRecord("sys_attachment");
	var signatureGR = new GlideRecord("signature_image");
	//var currentSysID = sysAttachGR.table_sys_id;
	sysAttachGR.addEncodedQuery("table_sys_id=" + current.table_sys_id);
	sysAttachGR.query();
	signatureGR.query();

 	gs.info("Current Sys ID::::::::::::::" + current.table_sys_id);
	gs.info("Signaure Image Document :::::::" + signatureGR.document);

	//if (tableNameCurrent == "x_58872_needit_needit_task")
 		//{

	if(sysAttachGR.next())
		{	glideSysAttachment.copy('signature_image',current.table_sys_id,'x_58872_needit_needit_task',signatureGR.document);
			gs.info("Base 64 Encoded Sign :::::::::" + glideSysAttachment.getContentBase64(current.table_sys_id));

		}
 	//	}

	//var signatureGR = new GlideRecord("signature_image");
	//signatureGR.addQuery();

})(current, previous);
