sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	 gs.info("Current Sys ID Sys Attachment Table::::::::::::::" + current.sys_id);
 	//var glideSysAttachment = new GlideSysAttachment();
 	var sysAttachGR = new GlideRecord("sys_attachment");
	var signatureGR = new GlideRecord("signature_image");
// 	//var currentSysID = sysAttachGR.table_sys_id;
	sysAttachGR.addEncodedQuery("sys_id=" + current.sys_id);
	sysAttachGR.query();

	if(sysAttachGR.next())
		{
			gs.info("Table Sys ID For signature table record :::::::::" + sysAttachGR.table_sys_id);
			//gs.info("NeedIt Record for signature table record::::::::" + sysAttachGR.document);
			signatureGR.addEncodedQuery("sys_id=" + sysAttachGR.table_sys_id);
			signatureGR.query();
			if (signatureGR.next())
				{
					gs.info ("Signature Image NeedIt Record Sys Id :::::::::" + signatureGR.document);
					gs.info("Signature Image NeedIt Table ::::::::" + signatureGR.table);
					var signatureUtils = new x_58872_needit.SignatureUtils();
					signatureUtils.getNeedItRecord(sysAttachGR.table_sys_id,signatureGR.document);
				}
		}

})(current, previous);
