sys_script_include.script:
var SignatureUtils = Class.create();
SignatureUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	getNeedItRecord : function(SignatureSysID,NeedItRecord)
	{
		gs.info("Signature Name sys id passed to Script include ::::::::::" + SignatureSysID);
		gs.info("NeedIt Table Record for corresponding signature ::::::::::" + NeedItRecord);
		var signatureStr = '';
		var attachmentRecord = new GlideRecord("sys_attachment");
		attachmentRecord.addEncodedQuery("content_type=image/png^table_sys_id=" + SignatureSysID);
		attachmentRecord.query();

		if (attachmentRecord.next())
			{
				var glideSysAttachment = new GlideSysAttachment();
				gs.info("Base 64 Encoded Sign :::::::::" + glideSysAttachment.getContentBase64(attachmentRecord));
				glideSysAttachment.copy('signature_image',SignatureSysID,'x_58872_needit_needit_task',NeedItRecord);
				gs.info("Signature Should have been attached");
			}

	},

    type: 'SignatureUtils'
});
