// 	}

	var duesoonNITasks = new GlideRecord('x_58872_needit_needit_task');	 
	duesoonNITasks.addQuery('due_date','>=',rightNow);
	duesoonNITasks.addQuery('due_date','<',gs.hoursAgo(-24));
	duesoonNITasks.addQuery('state','!=',3);
	duesoonNITasks.query();
	while (duesoonNITasks.next())
		{
			gs.info("DueSoon NeedIt Tasks :::::" + duesoonNITasks.number);
			gs.eventQueue('x_58872_needit.dueSoonNeedItTasks',duesoonNITasks,duesoonNITasks.number,gs.getUserName(),'dueSoonNeedItTasks_queue');
		}
